import React from 'react';

import './styles/Badge.css';
import confLogo from '../images/Raven-With-Key-Minus-Raven-And-Key-Flowers.svg';
import flowers from '../images/Raven-With-Key-Minus-Raven-And-Key-Flowers.svg';

class Badge extends React.Component {
  render() {
    return (
    <div className="Badge">
        <div className="Badge__header">
          <img src={confLogo} alt="cabecera de presentación" />
          <h5 align-items center>Let's do something nice!</h5>
          <img src={flowers} alt="flowers" justify-content left />
        </div>

        <div className="Badge__section-name">
          <img
          className="Badge__avatar"
          src="https://secure.gravatar.com/avatar/9fcf7ea3f01e02a5b2665fbbfa399e6a?s=100&r=g"
          alt="Lucía Sánchez Hernández SEO y Frontend" />
          <h1>Lucía A. <br/> Sánchez H.</h1>
        </div>

        <div className="Badge__section-info">
          <h3>Frontend Developer Junior</h3>
          <div>@Lu_San2</div>
        </div>

        <div className="Badge__footer">
          <p>#ReactLover</p>
        </div>
    </div>)
  }
}

export default Badge;
