import React from 'react';

import './styles/Badge.css';
import confLogo from '../images/header.png';

class Badge extends React.Component {
  render() {
    return (
    <div className="Badge">
        <div className="Badge__header">
          <img src={confLogo} alt="cabecera de presentación" />
        </div>

        <div>
          <img src="https://www.gravatar.com/avatar?d=identicon" alt="Lucía Sánchez Hernández SEO y Frontend" />
          <h1>Lucía A. <br/> Sánchez H.</h1>
        </div>

        <div>
          <p>SEO Specialist and Junior Front-end Developer</p>
          <p>@Lu_San2</p>
        </div>

        <div>
          <p>#ReactLover</p>
        </div>
    </div>)
  }
}

export default Badge;
